import {useState} from 'react'
import './ExpenseForm.css'
function ExpenseForm() {
    const [enteredTitle, setEnterdTitle] = useState('test')
    const [enteredAmount, setEnterdAmount] = useState()
    const [enteredDate, setEnterdDate] = useState()
    const [userInput, setUserInput] = useState({
        enteredTitle : '',
        enteredAmount : '',
        enteredDate : ''
    })
    const titleChangeHandler = (e) => {
        setUserInput((prevState) => {
            return {...prevState, enteredTitle : e.target.value}
        })
        setEnterdTitle(e.target.value)
        setEnterdAmount(e.target.value)
    }
    const amountChangeHandler = (e) => {
        setUserInput((prevState) => {
            return {...prevState, enteredAmount : e.target.value}
        })
        // console.log(e.target.value);
    }
    const dateChangeHandler = (e) => {
        setUserInput((prevState) => {
            return {...prevState, enteredDate : e.target.value}
        })
        // console.log(e.target.value);
    }
    console.log(userInput,enteredTitle);

  return (
    <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange={titleChangeHandler} value={userInput.enteredTitle} className="" />
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" onChange={amountChangeHandler} value={userInput.enteredAmount} min='0.01' step='0.1' max='10000' className="" />
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" onChange={dateChangeHandler} value={userInput.enteredDate} min='2020-01-01' max='2024-02-02' />
            </div>
            <div className="new-expense__actions">
                <button type="submit" className="">Add Expense</button>
            </div>
        </div>
    </form>
  )
}

export default ExpenseForm
