import {useState} from 'react';

import Card from '../UI/Card';
import ExpenseItem from './ExpenseItem.js';
import './Expenses.css'
import ExpensesFilter from './ExpensesFilter'
const ListItems = (props) => {
  const [selectedYear, setSelectedYear] = useState('All')
  const filterChangeHandler = (value) => {
    setSelectedYear(value)
  }
  const filterdDataHandler = props.data.filter(expense => {
    if (selectedYear === 'All') 
    return true
    return expense.date.getFullYear().toString() === selectedYear
  })
  let contentList = <p>No Data</p>
  if(filterdDataHandler.length > 0)
    contentList= filterdDataHandler.map(expense => {
      return (
        <ExpenseItem key={expense.id} date={expense.date} title={expense.title} amount={expense.amount}></ExpenseItem>
      )
    })
  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={selectedYear}    
        onChangeFilter={filterChangeHandler}
      />
      {contentList}
    </Card>
    );
};

export default ListItems;
