import {useState} from 'react'
import './ExpenseForm.css'
function ExpenseForm() {
    const [enteredTitle, setEnteredTitle] = useState('')
    const [enteredAmount, setEnteredAmount] = useState('')
    const [enteredDate, setEnteredDate] = useState('')
    // const [userInput, setUserInput] = useState({
    //     enteredTitle : '',
    //     enteredAmount : '',
    //     enteredDate : ''
    // })
    const titleChangeHandler = (e) => {
        // setUserInput((prevState) => {
        //     return {...prevState, enteredTitle : e.target.value}
        // })
        setEnteredTitle(e.target.value)
    }
    const amountChangeHandler = (e) => {
        // setUserInput((prevState) => {
        //     return {...prevState, enteredAmount : e.target.value}
        // })
        // console.log(e.target.value);
        setEnteredAmount(+e.target.value)
    }
    const dateChangeHandler = (e) => {
        // setUserInput((prevState) => {
        //     return {...prevState, enteredDate : e.target.value}
        // })
        // console.log(e.target.value);
        setEnteredDate(e.target.value)
    }
    const submitHandler = (e) => {
        e.preventDefault();
        const data = {
            title : enteredTitle,
            amount : enteredAmount,
            date : enteredDate
        }
        setEnteredDate('')
        setEnteredTitle('')
        setEnteredAmount('')
        console.log(data);
    }
    console.log('aa');

  return (
    <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange={titleChangeHandler} value={enteredTitle} className="" />
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" onChange={amountChangeHandler} value={enteredAmount} min='0.01' step='0.1' max='10000' className="" />
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" onChange={dateChangeHandler} value={enteredDate} min='2020-01-01' max='2024-02-02' />
            </div>
            <div className="new-expense__actions">
                <button type="submit" className="">Add Expense</button>
            </div>
        </div>
    </form>
  )
}

export default ExpenseForm
