import {useState} from 'react'
import './ExpenseForm.css'
function ExpenseForm() {
    const [enteredTitle, setEnterdTitle] = useState('test')
    const [enteredAmount, setEnterdAmount] = useState('test')
    const [enteredDate, setEnterdDate] = useState('test')
    const titleChangeHandler = (e) => {
        console.log(e.target.value);
        setEnterdTitle(e.target.value)
    }
    const amountChangeHandler = (e) => {
        console.log(e.target.value);
        setEnterdAmount(e.target.value)
    }
    const dateChangeHandler = (e) => {
        console.log(e.target.value);
        setEnterdDate(e.target.value)
    }
  return (
    <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange={titleChangeHandler} value={enteredTitle} className="" />
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min='0.01' step='0.1' max='10000' className="" />
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min='2020-01-01' max='2024-02-02' />
            </div>
            <div className="new-expense__actions">
                <button type="submit" className="">Add Expense</button>
            </div>
        </div>
    </form>
  )
}

export default ExpenseForm
