import { useState, useEffect } from 'react'
import Expenses from '../Expenses/Expenses'
import NewExpense from '..//NewExpense/NewExpense'

function ExpensesApp() {
    const [data, setData] = useState([])
    useEffect(() =>{
      let expense_data = []
      // Parse the serialized data back into an aray of objects
      expense_data = JSON.parse(localStorage.getItem('expense_data')) || [];
      // Push the new data (whether it be an object or anything else) onto the array
      expense_data.push(data);
      // Alert the array value
      // Re-serialize the array back into a string and store it in localStorage
      localStorage.setItem('expense_data', JSON.stringify(expense_data));
    },[data])
    const saveNewData = newData => {
      setData((prevState) => {
        return [newData, ...prevState]
      })
    }
    
    const deleteHandler = (id) => {
      if(data.length > 0){
        const filterdDataHandler = data.filter(expense => {
          if (expense.id !== id)
            return true;
          return false;
        })
        setData(filterdDataHandler)  
      }
    }
    return (
      <div className="App">
        <NewExpense saveNewData={saveNewData} />
        <Expenses onDelete={deleteHandler} data={data} />
      </div>
    );
}

export default ExpensesApp
